import React, { Component } from 'react'
import  {Form, FormGroup, Label, Input} from 'reactstrap';
import RenderAlbum from './RenderAlbum';

export class Album extends Component {

    constructor(props) {
        super(props)

        this.state = {
             album: '',
             albumdata: '',
             errorMessage: ''
        };

    // binding object
    this.HandleChange = this.HandleChange.bind(this);
    this.HandleSubmit = this.HandleSubmit.bind(this);
    }

      HandleChange  = (event) => {
          this.setState({
              album: event.target.value
          });
     }

      HandleSubmit = (event) => {
          event.preventDefault();
          fetch(`https://jsonplaceholder.typicode.com/albums/${this.state.album}/photos`)
                .then(response => {
                    if(response.ok){
                        return response.json();
                    }
                    throw response
                })
                .then(data  =>this.setState({
                    albumdata: data,
                    errorMessage: ''
                }))
                .catch(error => this.setState({
                    albumdata: '',
                    errorMessage: error
                }))

       // clear form data after submittion

     }
     HandleFocus = () =>{
         this.setState({
             album: ''
         });
     }

    render() {
        return (

<div class="topnav">
<a class="active" href="#home"><br></br></a>

<div class="search-container">
  <form>


        <Input type="text" name="album_id" id="albumId" placeholder="search by  album id" value={this.state.album} onChange={this.HandleChange} onFocus={this.HandleFocus} />


      <button type="button"  onClick = {this.HandleSubmit}>Get Album Photos By Id</button>

  </form>
  <br></br>
  <RenderAlbum albums ={this.state.albumdata} errorMessage = {this.state.errorMessage}></RenderAlbum>
</div>
</div>     



        )
    }
}

export default Album
